import React from 'react';
import { Redirect } from 'react-router-dom';
import UserService from '../../services/user-service';
import { Alert, Button, Card, CardBody, Input } from 'reactstrap';

class RegisterForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            color: '',
            message: '',
            display: false,
            RetypedPassword: ''
        }
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const { email, password } = this.state;
        UserService.register(email, password).then((res) => {
            if (!res.status) {
                this.setState({ display: true, color: 'danger', message: res.message });
            } else {
                this.setState({ color: 'success', display: true, message: res.message });
                return <Redirect to='/servers' />
            }
        })
    }

    setRedirect = () => {
        this.setState({
            redirect: true
        })
    }

    redirectLogin = (e) => {
        if (this.state.redirect) {
            return <Redirect to='/login' />
        }
    }

    render() {
        return (
            <div className="bg-login">
                <div className="content">
                    <div className="login-form">
                        <Card>
                            <CardBody>
                                <h1 className="text-center" style={{ fontWeight: 'bold', color: '#1720298a' }}>
                                    Sign up
                                </h1>
                                <Alert color={this.state.color} isOpen={this.state.diplay} role="alert">{this.state.message}</Alert>
                                <form onSubmit={this.onSubmit}>
                                    <Input
                                        type="email"
                                        name="email"
                                        placeholder="Email"
                                        className='form-control'
                                        value={this.state.email}
                                        onChange={this.onChange}
                                    />
                                    <br></br>
                                    <Input
                                        type="password"
                                        name="password"
                                        className='form-control'
                                        placeholder="Password"
                                        value={this.state.password}
                                        onChange={this.onChange}
                                        autoComplete="new-password"
                                    />
                                    <br></br>
                                    <input
                                        className='form-control'
                                        type="password"
                                        name="RetypedPassword"
                                        placeholder="Retype Password"
                                        autoComplete="off"
                                        value={this.state.RetypedPassword}
                                        onChange={this.onChange}
                                    />
                                    <br />
                                    <hr />
                                    <Button className="btn-block" color="success" type="submit">Sign up</Button>
                                    <div>
                                        {this.redirectLogin()}
                                        <Button className="btn-block" color="light" onClick={this.setRedirect}><b>Login</b></Button>
                                    </div>
                                </form>
                            </CardBody>
                        </Card>
                    </div>
                </div >
            </div>
        );
    }
}
export default RegisterForm;
