import React, { Component } from "react";
import { Alert, Button, Card, CardBody, Col, Input, Modal, ModalHeader, ModalBody, ModalFooter, Row, Table } from 'reactstrap';
import UserService from '../../services/user-service';

import { createBrowserHistory } from 'history';
const history = createBrowserHistory();

class Admin extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      updated: false,
      message: '',
      modal: false,
      modalAddUser: false,
      modalRights: false,
      alert: false,
      color: '',
      users: []
    };

    this.toggle = this.toggle.bind(this);
    this.toggleAddUser = this.toggleAddUser.bind(this);
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  componentDidMount() {
    this.getUser();
  }

  toggle = (email, e) => {
    this.setState({
      modal: true, email: email
    });
    localStorage.setItem('toDelete', email);
  }

  toggleAddUser = () => {
    this.setState(prevState => ({ modalAddUser: !prevState.modalAddUser })
    );
  }

  toggleRights = (email, e) => {
    this.setState(prevState => ({ modalRights: !prevState.modalRights }));
    this.setState({ email: email });
    localStorage.setItem('toAdmin', email);
  }

  closeAlert = () => {
    this.setState({ modalAddUser: false, modal: false, modalRights: false, message: "", updated: false });
  }

  getUser = () => {
    let usersList = []
    UserService.getAllUsers().then((response) => {
      if (!response || response === 'undefined') {
        history.push('/server');
        window.location.reload();
      } else {
        response.forEach((line) => {
          if (!line) {
            history.push('/server');
          }
          usersList.push(line)
        });
      }
      this.setState({ users: usersList, modal: false });
    });
  }

  addUsers = () => {
    let email = this.state.email;
    let password = this.state.password;
    UserService.createUser(email, password).then((response) => {
      if (response.status === true) {
        this.getUser();
        this.setState({ modalAddUser: false, updated: true, message: response.message, color: 'success' });
      } else {
        this.setState({ modalAddUser: false, updated: true, message: response.message, color: 'danger' })
      }
    }).catch((err) => {
      this.setState({ alert: true, message: err, color: 'danger' })
    });
  }

  setAdmin = () => {
    let email = localStorage.getItem("toAdmin");
    UserService.setAdmin(email).then((response) => {
      if (response.status === true) {
        this.getUser();
        this.setState({ modalRights: false, updated: true, message: response.message, color: 'success' });
      } else {
        this.setState({ modalRights: false, updated: true, message: response.message, color: 'danger' });
      }
    }).catch((err) => {
      this.setState({ modalRights: false, updated: true, message: err, color: 'danger' });
    });
    localStorage.removeItem('toAdmin');
  }

  onDelete = () => {
    let email = localStorage.getItem('toDelete');
    UserService.deleteUser(email).then((response) => {
      if (response.data.status === true) {
        this.getUser();
        this.setState({ modal: false, updated: true, message: response.data.message, color: 'success' });
      } else {
        this.setState({ modal: false, updated: true, message: response.data.message, color: 'danger' });
      }
    }).catch((error) => {
      return (error);
    });
    localStorage.removeItem('toDelete');
  }

  render() {
    if (window.localStorage.getItem("role") !== "admin") {
      window.location.replace('/servers');
      window.reload(true);
    }
    let listUsers = this.state.users;
    const listUser = listUsers.map((list) =>
      <tr key={list.ID}>
        <td>{list.email}</td>
        <td align="right">{list.Role}</td>
        <td align="center" style={{ width: '160px' }}>
          <Button className="btn btn-info btn-outline btn-sm" size="sm">
            <i className="material-icons" style={{ fontSize: '17px' }} value={list.email} onClick={this.toggleRights.bind(this, list.email)}>settings</i>
          </Button>
          <Button className="btn btn-success btn-outline btn-sm" size="sm">
            <i className="material-icons" style={{ fontSize: '17px' }}>update</i>
          </Button>
          <Button className="btn btn-danger btn-outline btn-sm" size="sm" value={list.email} onClick={this.toggle.bind(this, list.email)}>
            <i className="material-icons" style={{ fontSize: '17px' }}>delete</i>
          </Button>
        </td>
      </tr>
    );
    return (
      <div className="container-fluid" style={{ height: '90vh', marginTop: '30px' }}>
        <Row>
          <Col sm={12} md={12} lg={12} xl={8}>
            <Card>
              <CardBody>
                <Row>
                  <Col xs={8}>
                    <h5 style={{ marginTop: '12px', marginBottom: '' }}> List users</h5>
                  </Col>
                  <Col xs={2}>
                    <Button className="btn btn-sm btn-block btn-success" onClick={this.toggleAddUser}>
                      <i className="material-icons" style={{ fontSize: '17px' }}>person_add</i>
                    </Button>
                  </Col>
                  <Col xs={2}>
                    <Button className="btn btn-sm btn-block btn-light" onClick={this.getUser}>
                      <i className="material-icons" style={{ fontSize: '17px' }}>update</i>
                    </Button>
                  </Col>
                </Row>
                <Alert color={this.state.color} isOpen={this.state.updated} toggle={this.closeAlert}>
                  {this.state.message}
                </Alert>
                <hr />
                <div className="table-container">
                  <Table responsive striped bordered hover size="md">
                    <tbody>
                      {listUser}
                    </tbody>
                  </Table>
                </div>
              </CardBody>
            </Card>
          </Col>
          <Col sm={12} md={12} lg={12} xl={4}>
            <Card>
              <CardBody>
                <h5>Profil</h5>
                <hr />
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Modal isOpen={this.state.modal} size="md">
          <ModalHeader toggle={this.closeAlert}><strong className="text-center">Are you sure ? </strong></ModalHeader>
          <ModalBody>
            <br />
            {this.state.email} will be definitely deleted.
            <br />
            <br />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.closeAlert}>Cancel</Button>
            <Button color="success" onClick={this.onDelete}>Delete</Button>
          </ModalFooter>
        </Modal>
        <Modal isOpen={this.state.modalAddUser} size="md">
          <ModalHeader toggle={this.closeAlert}><strong className="text-center">Create a new user </strong></ModalHeader>
          <ModalBody>
            <Input className='form-control'
              type="email"
              name="email"
              placeholder="Email"
              value={this.state.email}
              onChange={this.onChange}
            />
            <br></br>
            <Input
              className='form-control'
              type="password"
              name="password"
              placeholder="Password"
              value={this.state.password}
              onChange={this.onChange}
              />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.closeAlert}>Cancel</Button>
            <Button color="success" onClick={this.addUsers}>Create</Button>
          </ModalFooter>
        </Modal>
        <Modal isOpen={this.state.modalRights} size="md">
          <ModalHeader toggle={this.closeAlert}><strong className="text-center">Update Rights</strong></ModalHeader>
          <ModalBody>
            <br />
            <p><strong style={{ color: '#2ba5ac' }}>{this.state.email}</strong> will become an Admin. Are you sure? </p>
            <br />
            <br />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.closeAlert}>Cancel</Button>
            <Button color="success" onClick={this.setAdmin}>I'm sure!</Button>
          </ModalFooter>
        </Modal>

      </div>
    )
  }
}

export default Admin;
