import React from 'react';
import { Redirect } from 'react-router-dom';
import UserService from '../../services/user-service';
import { Alert, Button, Card, CardBody, Input } from 'reactstrap';

class ValidateForm {
    onSubmit = (e) => {
        e.preventDefault();
        const { email, password } = this.state;
        UserService.validate(email, password).then((res) => {
            if (res.status) {
                this.setState({ color: "success", display: true, message: res.message });
                return <Redirect to='/servers' />
            }
            else {
                this.setState({ color: "danger", display: true, message: res.message })
            }
        })
    }

    render() {
        return (
            <div className="bg-login">
                <div className="content">
                    <div className="login-form">
                        <Card>
                            <CardBody>
                                <h1 className="text-center" style={{ fontWeight: 'bold', color: '#1720298a' }}>
                                    Login
                                </h1>
                                <Alert color={this.state.color} isOpen={this.state.display}>{this.state.message}</Alert>
                                <form onSubmit={this.onSubmit}>
                                    <Input
                                        className='form-control'
                                        type="email"
                                        name="email"
                                        placeholder="Email"
                                        value={this.state.email}
                                        onChange={this.onChange}
                                        autoComplete="email"
                                    />
                                    <br></br>
                                    <Input
                                        className='form-control'
                                        type="password"
                                        name="password"
                                        placeholder="Password"
                                        value={this.state.Password}
                                        onChange={this.onChange}
                                        autoComplete="current-password"
                                    />
                                    <br />
                                    <hr />
                                    <Button className="btn-block" color="success" type="submit"><b>Login</b></Button>
                                    <div>
                                        {this.redirectRegister()}
                                        <Button className="btn-block" color="light" onClick={this.setRedirect}><b>Register</b></Button>
                                    </div>
                                </form>
                            </CardBody>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }

}

export default ValidateForm;
