import React from 'react';
// import { Redirect } from 'react-router-dom';
import UserService from '../../services/user-service';
import { Button, Card, CardBody } from 'reactstrap';

class ValidateForm extends React.Component {
    onSubmit = (e) => {
        e.preventDefault();
        const { email, password } = this.state;
        UserService.validate(email, password).then((res) => {
            if (res.status) {
                this.setState({ color: "success", display: true, message: res.message });
            }
            else {
                this.setState({ color: "danger", display: true, message: res.message })
            }
        })
    }

    componentWillMount() {
        let param =  this.props.location.query.email;
        console.log(param)
    } 

    render() {
        return (
            <div className="bg-login">
                <div className="content">
                    <div className="login-form">
                        <Card>
                            <CardBody>
                                <h1 className="text-center" style={{ fontWeight: 'bold', color: '#1720298a' }}>
                                    Valide
                                </h1>
                                <hr />
                                <Button className="btn-block" color="success" type="submit"></Button>
                            </CardBody>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }

}

export default ValidateForm;
