import React from 'react';
import { Redirect } from 'react-router-dom';
import UserService from '../../services/user-service';
import { Alert, Button, Card, CardBody } from 'reactstrap';

class ValidateForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            color: 'success',
            display: false,
            message: '',
            redirect: false
        }
    }

    getEmail = (url) => {
        let queryString = url ? url.split('?')[1] : window.location.search.slice(1);
        if (queryString) {
            let arr = queryString.split('&');
            for (let i = 0; i < arr.length; i++) {
                let a = arr[i].split('=');
                let paramName = a[0];
                if (paramName === 'token') {
                    localStorage.setItem('token', a[1]);
                }
                if (paramName === 'email') {
                    let paramValue = typeof (a[1]) === 'undefined' ? true : a[1];
                    paramName = paramName.toLowerCase();
                    return paramValue
                }
            }
        }
    }   

    onSubmit = (e) => {
        // console.log("test")
        e.preventDefault();
        let email = this.getEmail();
        UserService.validAccount(email).then((res) => {
            if (res.status) {
                this.setState({ color: "success", display: true, message: res.message });
                return <Redirect to={'/login'} />;
            }
            else {
                this.setState({ color: "danger", display: true, message: res.message })
            }
        });
    }

    render() {
        return (
            <div className="bg-login">
                <div className="content">
                    <div className="login-form">
                        <Card>
                            <CardBody>
                                <h1 className="text-center" style={{ fontWeight: 'bold', color: '#1720298a' }}>
                                    Valide your account
                                </h1>
                                <hr />
                                <Alert color={this.state.color} isOpen={this.state.display}>{this.state.message}</Alert>
                                <Button className="btn-block" color="success" onClick={this.onSubmit}>Finish !</Button>
                            </CardBody>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }

}

export default ValidateForm;
