import React, { Component } from "react";
import dateFormat from 'dateformat';
import UserService from "../../services/user-service.jsx"
import { Card, CardBody, Button, Row, Col, Table, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input, InputGroup, InputGroupAddon } from 'reactstrap';

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      message: '',
      modal: false
    };
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  componentDidMount() {
    this.getUsers();
  }

  getUsers = () => {
    let usersList = []
    UserService.getAllUsers().then((response) => {
      console.log(response)
      response.forEach((line) => {
        usersList.push(line)
      })
      this.setState({ users: usersList });
      if (this.state.users.length === 0 || this.users.servers === '') {
        this.setState({ message: "No server found", alertServer: true });
      }
    }).catch((err) => {
      this.setState({ message: err });
    });
  }

  toggle = () => {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  closeAlert = () => {
    this.setState({ message: "", alertFailOver: false, alertServer: false, modal: false });
  }

  render() {
    let list = this.state.users;
    const Users = list.map((lis) =>
      <tr key={lis.ID}>
        <td>{lis.ID}</td>
        <td>{lis.email}</td>
        <td>{ dateFormat(lis.CreateAt, "mmmm dS, yyyy")}</td>
      </tr>
    );
    return (
      <div className="container" style={{ height: '90vh', marginTop: '30px' }}>
        <Row>
          <Col sm={12}>
            <Card>
              <CardBody>
                <Row>
                  <Col xs={5}>
                    <h5 style={{ marginTop: '12px' }}> List Server</h5>
                  </Col>
                  <Col xs={6}>
                    <InputGroup>
                      <Input placeholder="Search..." style={{ marginTop: '10px' }} type="text"/>
                      <InputGroupAddon addonType="append" className="btn btn-sm btn-success">
                        <i className="material-icons" style={{ fontSize: '21px' }}>search</i>
                      </InputGroupAddon>
                    </InputGroup>
                  </Col>
                  <Col xs={1} align="right">
                    <button className="btn btn-sm btn-block btn-success pull-right" title="Refresh datas from server" >
                      <i className="material-icons" style={{ fontSize: '17px' }}>update</i>
                    </button>
                  </Col>
                </Row>

                <hr />
                <div className="table-container">
                  <Table striped bordered hover size="md">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Email</th>
                        <th>Created at</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Users}
                      <h1>{beer}</h1>
                    </tbody>
                  </Table>
                </div>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Form>
          <Modal isOpen={this.state.modal} >
          <Form>
              <ModalHeader toggle={this.closeAlert}><strong className="text-center">Add server configuration </strong></ModalHeader>
              <ModalBody>
                <FormGroup>
                  <Label for="exampleAddress">Address IP</Label>
                  <Input type="text" name="address" id="exampleAddress" placeholder="Address entrypoint" />
                </FormGroup>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Label for="user">User</Label>
                      <Input type="text" name="user" id="user" placeholder="Name (optional)" />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Label for="serverIp">Password</Label>
                      <Input type="password" name="password" id="password" placeholder="Password" />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={12}>
                    <FormGroup>
                      <Label for="fileName">File name</Label>
                      <Input type="text" name="fileName" id="fileName" />
                    </FormGroup>
                  </Col>
                </Row>
              </ModalBody>
              <ModalFooter>
                <Button color="secondary" onClick={this.closeAlert}>Cancel</Button>
                <Button color="success" onClick={this.onDelete}>Valide</Button>
              </ModalFooter>
              </Form>
          </Modal>
        </Form>
      </div>
    );
  }
}

export default Dashboard;
