import React, { Component } from "react";
import {
  Card,
  Col,
  CardBody,
  CardHeader,
  Button,
  Row,
  Container
} from "reactstrap";
import ServiceWorker from "../../services/user-service";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      message: "",
      beer: "",
      modal: false
    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  componentDidMount() {
    this.getTasks();
  }

  getTasks = () => {
    let listTasks = [];
    ServiceWorker.getTasks().then(response => {
      console.log(response);
      response.forEach(data => {
        listTasks.push({
          id: data._id,
          task: data.task,
          status: data.status
        });
        this.setState({tasks : listTasks})
      });
    }).catch((err) => {
      console.log(err)
    });
  };

  toggle = () => {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  };

  render() {
    let list = this.state.tasks;
    console.log(this.state.tasks)
    const lisTasks = list.map(x => (
      <CardBody>
        <Row>
          <h3>{x.task}</h3>
          <hr />
          <p>{x.status}</p>
        </Row>
      </CardBody>
    ));
    return (
      <>
        <Container fluid>
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <h1>List Tasks</h1>
                </CardHeader>
                {lisTasks}
              </Card>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Dashboard;
