import React from 'react';
import { NavLink } from 'react-router-dom';
import { Nav } from 'reactstrap';
import PerfectScrollbar from 'perfect-scrollbar';

let ps;
const logo = require('../../assets/logo.png');

class Sidebar extends React.Component {

    constructor(props) {
        super(props);
        this.activeRoute.bind(this);
    }

    activeRoute(routeName) {
        return this.props.location.pathname.indexOf(routeName) > -1 ? 'active' : '';
    }

    componentDidMount() {
        if (navigator.platform.indexOf('Win') > -1) {
            ps = new PerfectScrollbar(this.refs.sidebar, { suppressScrollX: true, suppressScrollY: false });
        }
    }

    componentWillUnmount() {
        if (navigator.platform.indexOf('Win') > -1) {
            ps.destroy();
        }
    }

    render() {
        const token = localStorage.getItem('token');
        return (
            <div className="sidebar" data-color="yellow" style={{ backgroundColor: '#172029' }}>
                <div className="logo" style={{ backgroundColor: '#172029' }}>
                    <a href="/servers">
                        <div className="logo-img" style={{ display: 'flex', justifyContent: 'center' }}>
                            <img src={logo} className="logo" alt="machine-over-logo" />
                        </div>
                    </a>
                </div>
                <div className="sidebar-wrapper" ref="sidebar" style={{ backgroundColor: '#172029' }}>
                    <Nav>
                        {
                            this.props.routes.map((prop, key) => {
                                const role = window.localStorage.getItem('role')
                                if (prop.redirect)
                                    return null;
                                if (role !== 'admin' && prop.name === 'Admin') {
                                    return null
                                }
                                if (prop.name === 'Login' || prop.name === 'Register') {
                                    return null
                                }
                                if (!token && prop.name === 'Account') {
                                    return null
                                }
                                return (
                                    <li className={this.activeRoute(prop.path) + (prop.pro ? " active active-pro" : "")} key={key}>
                                        <NavLink to={prop.path} className="nav-link" activeClassName="active">
                                            <i className={"now-ui-icons " + prop.icon}></i>
                                            <p>{prop.name}</p>
                                        </NavLink>
                                    </li>
                                );
                            })
                        }
                    </Nav>
                </div>
            </div>
        );
    }
}

export default Sidebar;
