import React, { Component } from "react";
import {
  Card,
  Col,
  CardBody,
  CardHeader,
  Button,
  Row,
  Container,
  Modal,
  ModalBody,
  ModalFooter
} from "reactstrap";
import ServiceWorker from "../../services/user-service";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      modal: false,
      backdrop: true
    };
  }

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  componentDidMount() {
    this.getTasks();
  }

  getTasks = () => {
    let listTasks = [];
    ServiceWorker.getTasks()
      .then(response => {
        response.forEach(data => {
          listTasks.push({
            id: data._id,
            task: data.task,
            status: data.status
          });
          this.setState({ tasks: listTasks });
        });
      })
      .catch(err => {
        console.log(err);
      });
  };

  toggle = () => {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  };

  render() {
    let list = this.state.tasks;
    const lisTasks = list.map(x => (
      <Card key={x}>
        <CardBody>
          <Col xs={10}>
            <h3>{x.task}</h3>
            <br></br>
            <p>{x.status}</p>
            <Button color="default" className="pull-right" size="md">
              Show
            </Button>
            <Button color="danger" className="pull-right" size="md">
              Delete
            </Button>
          </Col>
        </CardBody>
      </Card>
    ));
    return (
      <>
        <Container>
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <h1>List Tasks</h1>
                </CardHeader>
                {lisTasks}
              </Card>
            </Col>
          </Row>
        </Container>
        <Modal
          isOpen={this.state.modal}
          toggle={this.toggle}
          className={this.props.className}
          backdrop={this.state.backdrop}
        >
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggle}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      </>
    );
  }
}

export default Dashboard;
